import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Header.css';
import logo from '../images/logo.png';

const Header = () => {
  const navigate = useNavigate();

  const handleHomeClick = () => {
    navigate('/home');
  };

  const handleButtonClick = (route) => {
    navigate(route);
  };

  // Dropdown menu para Serviços
  const [isDropdownVisible, setDropdownVisible] = useState(false);
  const [visibleServices, setVisibleServices] = useState([]);

  const handleMouseEnter = () => {
    setDropdownVisible(true);
    setVisibleServices([]); // Resetar a lista visível
  };

  const handleMouseLeave = () => {
    setDropdownVisible(false);
    setVisibleServices([]); // Limpar itens visíveis ao sair
  };

  const servicesDropdown = [
    { label: 'Câmbio de Moedas Estrangeiras', route: '/servico1' },
    { label: 'Câmbio Comercial', route: '/servico2' },
    { label: 'Seguro de Viagem', route: '/servico3' },
    { label: 'Chip Internacional', route: '/servico4' },
    { label: 'Pacote de Viagens', route: '/servico5' },
    { label: 'Western Unions', route: '/servico6' },
    { label: 'Locação de Carros', route: '/servico7' },
  ];

  const buttonData = [
    { label: 'Sobre a Fortur', route: '/QuemSomos' },
    { label: 'Serviços', route: '/Services' },
    { label: 'Pacotes', route: '/Pacotes' },
    { label: 'Fale Conosco', route: '/FaleConosco' },
    { label: 'Blog', route: '/Blog' },
    { label: 'Câmbio Exchange', route: '/Cambio' },
  ];

  useEffect(() => {
    if (isDropdownVisible) {
      const timer = setInterval(() => {
        setVisibleServices((prev) => {
          if (prev.length < servicesDropdown.length) {
            return [...prev, servicesDropdown[prev.length]];
          } else {
            clearInterval(timer);
            return prev;
          }
        });
      }, 100); // Cada 0.1 segundo
      return () => clearInterval(timer);
    } else {
      setVisibleServices([]); // Limpar a lista visível quando o dropdown não está visível
    }
  }, [isDropdownVisible]);

  return (
    <header className="header">
      <div className="left-section">
        <img
          src={logo}
          alt="Home"
          className="logo"
          onClick={handleHomeClick}
        />
      </div>
      <div className="right-section">
        {buttonData.map((button, index) => (
          button.label === 'Serviços' ? (
            <div
              key={index}
              className="dropdown"
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
            >
              <button className="button">
                {button.label}
              </button>
              {isDropdownVisible && (
                <div className="dropdown-menu">
                  {visibleServices.map((service, idx) => (
                    <button
                      key={idx}
                      className="dropdown-item"
                      onClick={() => handleButtonClick(service.route)}
                    >
                      {service.label}
                    </button>
                  ))}
                </div>
              )}
            </div>
          ) : (
            <button
              key={index}
              className="button"
              onClick={() => handleButtonClick(button.route)}
            >
              {button.label}
            </button>
          )
        ))}
      </div>
    </header>
  );
};

export default Header;
