import React, { useState, useEffect } from "react";

// Componente completo de "QuemSomos" com o slider
const quemsomos = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const slides = [
    "https://via.placeholder.com/600x300?text=Foto+1",
    "https://via.placeholder.com/600x300?text=Foto+2",
    "https://via.placeholder.com/600x300?text=Foto+3",
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % slides.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [slides.length]);

  return (
    <div className="quem-somos-container">
      <h1>Quem Somos</h1>
      
      <div className="slider">
        <img src={slides[currentIndex]} alt={`Slide ${currentIndex + 1}`} />
      </div>

      <div className="dots">
        {slides.map((_, index) => (
          <span
            key={index}
            className={`dot ${index === currentIndex ? "active" : ""}`}
            onClick={() => setCurrentIndex(index)}
          ></span>
        ))}
      </div>

      <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit...</p>
    </div>
  );
};

export default quemsomos;
