import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Header.css'; // Importando o CSS
import logo from '../images/logo.png'

const Header = () => {
  const navigate = useNavigate();
  const [dropdownOpen, setDropdownOpen] = useState(false); // Controle do dropdown

  const handleHomeClick = () => {
    navigate('/home');
  };

  const handleButtonClick = (route) => {
    navigate(route);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const buttonData = [
    { label: 'Sobre a Fortur', route: '/QuemSomos' },
    { label: 'Pacotes', route: '/Pacotes' },
    { label: 'Fale Conosco', route: '/FaleConosco' },
    { label: 'Blog', route: '/Blog' },
    { label: 'Câmbio Exchange', route: '/Cambio' },
  ];

  const dropdownData = [
    { label: 'Serviço 1', route: '/servico1' },
    { label: 'Serviço 2', route: '/servico2' },
    { label: 'Serviço 3', route: '/servico3' },
    { label: 'Serviço 4', route: '/servico4' },
    { label: 'Serviço 5', route: '/servico5' },
    { label: 'Serviço 6', route: '/servico6' },
    { label: 'Serviço 7', route: '/servico7' },
  ];

  return (
    <header className="header">
      <div className="left-section">
        <img
          src={logo} // Substitua pela URL da sua imagem
          alt="Home"
          className="logo"
          onClick={handleHomeClick}
        />
      </div>
      <div className="right-section">
        {buttonData.map((button, index) => (
          <button
            key={index}
            className="button"
            onClick={() => handleButtonClick(button.route)}
          >
            {button.label}
          </button>
        ))}
        
        {/* Botão de "Serviços" com dropdown */}
        <div
          className="button services-button"
          onMouseEnter={toggleDropdown}
          onMouseLeave={toggleDropdown}
        >
          Serviços
          {dropdownOpen && (
            <div className="dropdown">
              {dropdownData.map((item, index) => (
                <button
                  key={index}
                  className="dropdown-item"
                  onClick={() => handleButtonClick(item.route)}
                >
                  {item.label}
                </button>
              ))}
            </div>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
