import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Header.css'; // Importando o CSS
import logo from '../images/logo.png'

const Header = () => {
  const navigate = useNavigate();

  const handleHomeClick = () => {
    navigate('/home');
  };

  const handleButtonClick = (route) => {
    navigate(route);
  };

  const buttonData = [
    { label: 'Sobre a Fortur', route: '/luminosa' },
    { label: 'Serviços', route: '/astoria' },
    { label: 'Pacotes', route: '/serenata' },
    { label: 'Fale Conosco', route: '/bravura' },
    { label: 'Blog', route: '/maritima' },
    { label: 'Câmbio Exchange', route: '/veloz' },
  ];

  return (
    <header className="header">
      <div className="left-section">
        <img
          src={logo} // Substitua pela URL da sua imagem
          alt="Home"
          className="logo"
          onClick={handleHomeClick}
        />
      </div>
      <div className="right-section">
        {buttonData.map((button, index) => (
          <button
            key={index}
            className="button"
            onClick={() => handleButtonClick(button.route)}
          >
            {button.label}
          </button>
        ))}
      </div>
    </header>
  );
};

export default Header;
