import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Header.css'; // Importando o CSS
import logo from '../images/logo.png';

const Header = () => {
  const navigate = useNavigate();
  const [isDropdownVisible, setDropdownVisible] = useState(false);
  const [isSidebarVisible, setSidebarVisible] = useState(false);

  const handleHomeClick = () => {
    navigate('/home');
  };

  const handleButtonClick = (route) => {
    navigate(route);
  };

  const handleMouseEnter = () => {
    setDropdownVisible(true);
  };

  const handleMouseLeave = () => {
    setDropdownVisible(false);
  };

  const toggleSidebar = () => {
    setSidebarVisible(!isSidebarVisible);
  };

  const servicesDropdown = [
    { label: 'Câmbio de Moedas Estrangeiras', route: '/servico1' },
    { label: 'Câmbio Comercial', route: '/servico2' },
    { label: 'Seguro de Viaigem', route: '/servico3' },
    { label: 'Chip Internacional', route: '/servico4' },
    { label: 'Pacote de Viagens', route: '/servico5' },
    { label: 'Western Unions', route: '/servico6' },
    { label: 'Locação de Carros', route: '/servico7' },
  ];

  const buttonData = [
    { label: 'Sobre a Fortur', route: '/QuemSomos' },
    { label: 'Serviços', route: '/Services' },
    { label: 'Pacotes', route: '/Pacotes' },
    { label: 'Fale Conosco', route: '/FaleConosco' },
    { label: 'Blog', route: '/Blog' },
    { label: 'Câmbio Exchange', route: '/Cambio' },
  ];

  return (
    <header className="header">
      <div className="left-section">
        <img
          src={logo}
          alt="Home"
          className="logo"
          onClick={handleHomeClick}
        />
      </div>
      <div className="right-section">
        <button className="hamburger" onClick={toggleSidebar}>
          &#9776; {/* Ícone de três tracinhos */}
        </button>
        {buttonData.map((button, index) => (
          button.label === 'Serviços' ? (
            <div
              key={index}
              className="dropdown"
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
            >
              <button className="button">
                {button.label}
              </button>
              {isDropdownVisible && (
                <div className="dropdown-menu">
                  {servicesDropdown.map((service, idx) => (
                    <button
                      key={idx}
                      className="dropdown-item"
                      onClick={() => handleButtonClick(service.route)}
                    >
                      {service.label}
                    </button>
                  ))}
                </div>
              )}
            </div>
          ) : (
            <button
              key={index}
              className="button"
              onClick={() => handleButtonClick(button.route)}
            >
              {button.label}
            </button>
          )
        ))}
      </div>
      {isSidebarVisible && (
        <div className="sidebar">
          {buttonData.map((button, index) => (
            <div key={index}>
              {button.label === 'Serviços' ? (
                <div
                  className="dropdown"
                  onMouseEnter={handleMouseEnter}
                  onMouseLeave={handleMouseLeave}
                >
                  <button className="button">
                    {button.label}
                  </button>
                  {isDropdownVisible && (
                    <div className="dropdown-menu">
                      {servicesDropdown.map((service, idx) => (
                        <button
                          key={idx}
                          className="dropdown-item"
                          onClick={() => handleButtonClick(service.route)}
                        >
                          {service.label}
                        </button>
                      ))}
                    </div>
                  )}
                </div>
              ) : (
                <button
                  className="button"
                  onClick={() => handleButtonClick(button.route)}
                >
                  {button.label}
                </button>
              )}
            </div>
          ))}
        </div>
      )}
    </header>
  );
};

export default Header;
